import {ADD_STOCK, UPDATE_STOCK, SET_CURRENT, GRAB_STOCK_TO_UPDATE} from '../types';

export default (state, action) => {
  switch (action.type) {
    case SET_CURRENT:
      return {
        ...state,
        currentStock: state.currentStocks.filter(stock => stock.symbol === action.payload)[0]
      };

    case GRAB_STOCK_TO_UPDATE:
      return {
        ...state,
        stockToUpdate: state.currentStocks.find(stock => stock.symbol === action.payload),
        currentStocks: state.currentStocks.filter(stock => stock.symbol !== action.payload)
      };
    case UPDATE_STOCK:
      return {
        ...state,
        stockToUpdate: {...state.stockToUpdate[shareAmount], shareAmount: 10}
      };
    case ADD_STOCK:
      return {
        ...state,
        currentStocks: [...state.currentStocks, action.payload] // making a copy and adding the payload
      };
    default:
      return state;
  }
};
