import React, {useReducer} from 'react';
import StockReducer from './stockReducer';
import StockContext from './stockContext';
import {ADD_STOCK, UPDATE_STOCK, SET_CURRENT, GRAB_STOCK_TO_UPDATE} from '../types';

const StockState = props => {
  const initialState = {
    currentCash: 5000,
    portfolioCash: 0,
    currentStocks: [
      {
        symbol: 'AAPL',
        shareAmount: 7,
        // sharePrice is the current price, has to be red or green if it is over/under open price
        sharePrice: 2000
      },
      {
        symbol: 'AMZN',
        shareAmount: 7,
        sharePrice: 5000
      },
      {
        symbol: 'MSFT',
        shareAmount: 7,
        sharePrice: 300
      }
    ],
    stockToUpdate: null
  };

  const [state, dispatch] = useReducer(StockReducer, initialState);

  // Add Stock

  // Have to add the stocks current price in BuyStock
  const addStock = stock => {
    dispatch({
      type: ADD_STOCK,
      payload: stock
    });
  };

  const setCurrent = symbol => {
    dispatch({
      type: SET_CURRENT,
      payload: symbol
    });
  };

  const updateStock = stock => {
    // Find index of stock to update
    const stockToUpdate = state.currentStocks.findIndex(stock => stock.symbol === stockToAdd.symbol);
    stockToUpdate.shareAmount += Number(stockToAdd.shareAmount);

    dispatch({
      type: UPDATE_STOCK,
      payload: stock
    });
  };

  /*  Helpers */

  // Check if Stock exists
  const stockExists = symbol => {
    var stockExists = state.currentStocks.some(stock => {
      return symbol == stock.symbol;
    });
    return stockExists;
  };

  // set stockToUpdate and remove updated stock from list ; keep immutability
  const grabStockToUpdate = symbol => {
    dispatch({
      type: GRAB_STOCK_TO_UPDATE,
      payload: symbol
    });
  };

  return (
    <StockContext.Provider
      value={{
        currentCash: state.currentCash,
        portfolioCash: state.portfolioCash,
        currentStocks: state.currentStocks,
        currentStock: state.currentStock,
        stockToUpdate: state.stockToUpdate,
        addStock,
        stockExists,
        updateStock,
        setCurrent
      }}
    >
      {props.children}
    </StockContext.Provider>
  );
};

export default StockState;
