import {ADD_STOCK, UPDATE_STOCK, SET_CURRENT} from '../types';

export default (state, action) => {
  switch (action.type) {
    case SET_CURRENT:
      return {
        ...state,
        currentStock: state.currentStocks.map(stock => {
          if (stock.symbol === action.payload) {
            currentStock = stock;
          }
        })
      };
    case UPDATE_STOCK:
      return {
        ...state
      };
    case ADD_STOCK:
      return {
        ...state,
        currentStocks: [...state.currentStocks, action.payload] // making a copy and adding the payload
      };
    default:
      return state;
  }
};
