import React, {Component} from 'react';
import axios from 'axios';

class BuyStock extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quantity: ''
    };
  }

  handleChange = event => {
    this.setState({quantity: event.target.value});
    console.log(event.target.value);
  };
  handleSubmit = event => {
    event.preventDefault();
    alert('SUBMITTED SUCCESSFULLY: ' + this.state.quantity);
  };

  render() {
    return (
      <div>
        <form id='stock-form' onSubmit={this.handleSubmit}>
          <h1>Cash - $5000.00</h1>
          <input type='text' className='stock-control' placeholder='Ticker' />
          <input
            type='text'
            className='stock-control'
            placeholder='Qty'
            value={this.state.quantity}
            onChange={this.handleChange}
          />
          <input type='submit' className='stock-control-submit' value='Buy' />
        </form>
      </div>
    );
  }
}

export default BuyStock;
